mapper.update:
UPDATE hotel_review
SET review_star = #{review_star}, review_cnt = #{review_cnt}
WHERE ireview = #{ireview}

UPDATE review_img
SET review_img = #{review_img}
WHERE ireview = #{ireview}

mapper.select:
SELECT CEIL(COUNT(ihotel) / #{recordCnt})
FROM hotel_info
WHERE h_location = #{ hLocation }
